import "./styles.css";
import { useState, useEffect } from "react";
import { FormBuilder } from "react-formio";

export default function App() {
  const [display, setDisplay] = useState("wizard");
  const [schema, setSchema] = useState({
    display: "wizard",
  });

  useEffect(() => {
    if (schema && schema.display !== display) {
      setSchema({ ...schema, display });
    }
  }, [schema, display]);

  return (
    <>
    <div className="header-bar">
     <img className="logo" src="https://s3-eu-west-1.amazonaws.com/static.campusjaeger.de/company/photo/36261/36261-210115094357.png" alt="Logo" width="150" />
      <div className="App container py-3">  
      <div className="row">
       <div className="col col-sm-2 offset-sm-3 mb-3 text-center">
          <label className="form-label">Display as</label>
          <select
            className="form-control"
            onChange={(e) => setDisplay(e.target.value)}
            value={display}>
            <option value="form">Form</option>
            <option value="wizard">Wizard</option>
          </select>
        </div>
      </div>
      <FormBuilder form={schema} onChange={setSchema} />
    </div>
    </div>   
    </>
  );
}
