import * as React from "react";
import { withRouter } from "react-router-dom";
import {
  Drawer,
  DrawerContent,
  DrawerItem,
} from "@progress/kendo-react-layout";
import { Button } from "@progress/kendo-react-buttons";
import * as FaIcons from "react-icons/fa";
const items = [
  {
    title: "Home",
    path: "/",
    icon: <AiIcons.AiFillHome />,
    cName: "nav-text"
  },
  {
    title: "FormBuilder",
    path: "/FormBuilder",
    icon: <IoIcons.IoIosPaper />,
    cName: "nav-text"
  },
  {
    title: "Products",
    path: "/products",
    icon: <FaIcons.FaCartPlus />,
    cName: "nav-text"
  },
  {
    title: "Team",
    path: "/team",
    icon: <IoIcons.IoMdPeople />,
    cName: "nav-text"
  },
  {
    title: "Messages",
    path: "/",
    icon: <FaIcons.FaEnvelopeOpenText />,
    cName: "nav-text"
  },
  {
    title: "Support",
    path: "/support",
    icon: <IoIcons.IoMdHelpCircle />,
    cName: "nav-text"
  },
];

const CustomItem = (props) => {
  return (
    <DrawerItem {...props}>
      <span className={"k-icon flag " + props.flag} />
      <div className="item-descr-wrap">
        <div>{props.text}</div>
        <span className="item-descr">Capital of {props.country}</span>
      </div>
    </DrawerItem>
  );
};

const DrawerContainer = (props) => {
  const [expanded, setExpanded] = React.useState(true);

  const handleClick = () => {
    setExpanded(!expanded);
  };

  const onSelect = (e) => {
    props.history.push(e.itemTarget.props.route);
  };

  const setSelectedItem = (pathName) => {
    let currentPath = items.find((item) => item.route === pathName);

    if (currentPath.text) {
      return currentPath.text;
    }
  };

  let selected = setSelectedItem(props.location.pathname);
  return (
    <div>
      <div className="custom-toolbar">
        <Button icon="menu" look="flat" onClick={handleClick} />
        <span className="title">Weather Forecast in Europe</span>
      </div>
      <Drawer
        expanded={expanded}
        mode="push"
        mini={true}
        width={175}
        items={items.map((item) => ({
          ...item,
          selected: item.text === selected,
        }))}
        item={CustomItem}
        onSelect={onSelect}
      >
        <DrawerContent>{props.children}</DrawerContent>
      </Drawer>
    </div>
  );
};

export default withRouter(DrawerContainer);
